import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Alert,
  Chip
} from '@mui/material';
import { useDropzone } from 'react-dropzone';

const JobDetails = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    jobProfile: '',
    jobSubProfile: '',
    jobDescription: '',
    companyName: '',
    difficulty: '',
    cvFile: null
  });
  const [error, setError] = useState('');

  const jobProfiles = {
    'AI/ML Engineering': [
      'Machine Learning Engineer',
      'Deep Learning Engineer',
      'NLP Engineer',
      'Computer Vision Engineer',
      'Data Scientist',
      'AI Research Engineer'
    ],
    'Software Engineering': [
      'Frontend Developer',
      'Backend Developer',
      'Full Stack Developer',
      'DevOps Engineer',
      'Mobile App Developer',
      'System Architect',
      'QA Engineer'
    ],
    'Data Engineering': [
      'Data Engineer',
      'ETL Developer',
      'Big Data Engineer',
      'Data Warehouse Engineer'
    ],
    'Cybersecurity': [
      'Security Analyst',
      'Penetration Tester',
      'Security Engineer',
      'SOC Analyst'
    ],
    'Cloud Engineering': [
      'AWS Engineer',
      'Azure Engineer',
      'GCP Engineer',
      'Cloud Architect'
    ],
    'Product Management': [
      'Product Manager',
      'Technical Product Manager',
      'Product Owner'
    ]
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleProfileChange = (e) => {
    setFormData({
      ...formData,
      jobProfile: e.target.value,
      jobSubProfile: '' // Reset sub-profile when main profile changes
    });
  };

  const onDrop = (acceptedFiles) => {
    if (acceptedFiles.length > 0) {
      setFormData({
        ...formData,
        cvFile: acceptedFiles[0]
      });
    }
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/msword': ['.doc'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx']
    },
    multiple: false
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.jobProfile || !formData.jobDescription || !formData.difficulty) {
      setError('Please fill in all required fields');
      return;
    }

    // Store form data in localStorage for now
    // In a real app, you'd send this to your backend
    localStorage.setItem('jobDetails', JSON.stringify(formData));
    navigate('/permissions');
  };

  return (
    <Container component="main" maxWidth="md" sx={{ mt: 4 }}>
      <Paper elevation={3} sx={{ p: 4 }}>
        <Typography component="h1" variant="h4" align="center" gutterBottom>
          Job Interview Details
        </Typography>
        <Typography variant="subtitle1" align="center" color="text.secondary" sx={{ mb: 3 }}>
          Please provide details about the position you're applying for
        </Typography>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 1 }}>
          <FormControl fullWidth margin="normal" required>
            <InputLabel>Job Profile</InputLabel>
            <Select
              name="jobProfile"
              value={formData.jobProfile}
              onChange={handleProfileChange}
              label="Job Profile"
            >
              {Object.keys(jobProfiles).map((profile) => (
                <MenuItem key={profile} value={profile}>
                  {profile}
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          {formData.jobProfile && (
            <FormControl fullWidth margin="normal">
              <InputLabel>Specialization (Optional)</InputLabel>
              <Select
                name="jobSubProfile"
                value={formData.jobSubProfile}
                onChange={handleInputChange}
                label="Specialization (Optional)"
              >
                {jobProfiles[formData.jobProfile].map((subProfile) => (
                  <MenuItem key={subProfile} value={subProfile}>
                    {subProfile}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          )}

          <TextField
            margin="normal"
            required
            fullWidth
            id="jobDescription"
            label="Job Description"
            name="jobDescription"
            multiline
            rows={6}
            placeholder="Paste the job description here..."
            value={formData.jobDescription}
            onChange={handleInputChange}
          />

          <TextField
            margin="normal"
            fullWidth
            id="companyName"
            label="Company Name (Optional)"
            name="companyName"
            placeholder="e.g., Google, Microsoft, etc."
            value={formData.companyName}
            onChange={handleInputChange}
          />

          <FormControl fullWidth margin="normal" required>
            <InputLabel>Difficulty Level</InputLabel>
            <Select
              name="difficulty"
              value={formData.difficulty}
              onChange={handleInputChange}
              label="Difficulty Level"
            >
              <MenuItem value="easy">Easy</MenuItem>
              <MenuItem value="medium">Medium</MenuItem>
              <MenuItem value="hard">Hard</MenuItem>
            </Select>
          </FormControl>

          <Box sx={{ mt: 3, mb: 2 }}>
            <Typography variant="h6" gutterBottom>
              Upload Your CV/Resume (Optional)
            </Typography>
            <Box
              {...getRootProps()}
              sx={{
                border: '2px dashed #ccc',
                borderRadius: 2,
                p: 3,
                textAlign: 'center',
                cursor: 'pointer',
                bgcolor: isDragActive ? '#f5f5f5' : 'transparent',
                '&:hover': {
                  bgcolor: '#f5f5f5'
                }
              }}
            >
              <input {...getInputProps()} />
              {formData.cvFile ? (
                <Box>
                  <Typography variant="body1" color="primary">
                    File selected: {formData.cvFile.name}
                  </Typography>
                  <Chip
                    label="Change file"
                    onClick={(e) => e.stopPropagation()}
                    sx={{ mt: 1 }}
                  />
                </Box>
              ) : (
                <Typography variant="body1" color="text.secondary">
                  {isDragActive
                    ? "Drop the file here..."
                    : "Drag & drop your CV here, or click to select (PDF, DOC, DOCX)"}
                </Typography>
              )}
            </Box>
          </Box>

          <Button
            type="submit"
            fullWidth
            variant="contained"
            sx={{ mt: 3, mb: 2 }}
          >
            Continue to Permissions Check
          </Button>
        </Box>
      </Paper>
    </Container>
  );
};

export default JobDetails;
