import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Webcam from 'react-webcam';
import {
  Container,
  Paper,
  Typography,
  Box,
  Button,
  Card,
  CardContent,
  LinearProgress,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Avatar,
  Fade,
  CircularProgress,
  Tooltip,
  IconButton,
  Badge,
  Divider,
  Alert
} from '@mui/material';
import {
  Mic as MicIcon,
  MicOff as MicOffIcon,
  Stop as StopIcon,
  PlayArrow as PlayIcon,
  Android as AIIcon,
  Videocam as VideocamIcon,
  VideocamOff as VideocamOffIcon,
  Timer as TimerIcon,
  Assessment as AssessmentIcon,
  CheckCircle as CheckCircleIcon,
  Error as ErrorIcon,
  Info as InfoIcon,
  VolumeUp as VolumeUpIcon,
  VolumeOff as VolumeOffIcon,
  Settings as SettingsIcon
} from '@mui/icons-material';

const Interview = () => {
  const navigate = useNavigate();
  const webcamRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const [isRecording, setIsRecording] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState('');
  const [questionNumber, setQuestionNumber] = useState(1);
  const [totalQuestions] = useState(5);
  const [isProcessing, setIsProcessing] = useState(false);
  const [recordedChunks, setRecordedChunks] = useState([]); // eslint-disable-line no-unused-vars
  const [showEndDialog, setShowEndDialog] = useState(false);
  const [interviewComplete, setInterviewComplete] = useState(false); // eslint-disable-line no-unused-vars
  const [recordingTime, setRecordingTime] = useState(0);
  const [isVideoEnabled, setIsVideoEnabled] = useState(true);
  const [isAudioEnabled, setIsAudioEnabled] = useState(true);
  const [questionStartTime, setQuestionStartTime] = useState(null);
  const [showTips, setShowTips] = useState(true);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [speechSupported, setSpeechSupported] = useState(false);
  const [questionSpoken, setQuestionSpoken] = useState(false);
  const [interviewPhase, setInterviewPhase] = useState('introduction'); // 'introduction', 'questions'
  const [userName, setUserName] = useState('');
  const [responseTimer, setResponseTimer] = useState(null);
  const [timeRemaining, setTimeRemaining] = useState(10);
  const [showMotivation, setShowMotivation] = useState(false);
  const [audioContext, setAudioContext] = useState(null);
  const [analyser, setAnalyser] = useState(null);
  const [silenceTimer, setSilenceTimer] = useState(null);
  const [isUserSpeaking, setIsUserSpeaking] = useState(false);
  const [silenceThreshold, setSilenceThreshold] = useState(5000); // 5 seconds of silence

  // Mock questions for demo
  const mockQuestions = [
    "Tell me about yourself and your background in software development.",
    "Can you explain a challenging project you've worked on and how you overcame the difficulties?",
    "What are your strengths and weaknesses as a developer?",
    "Why are you interested in this position?",
    "Do you have any questions for us?"
  ];

  useEffect(() => {
    // Check if speech synthesis is supported
    if ('speechSynthesis' in window) {
      setSpeechSupported(true);
    }

    // Start with introduction
    startInterviewIntroduction();
  }, []);

  // Recording timer
  useEffect(() => {
    let interval;
    if (isRecording) {
      interval = setInterval(() => {
        setRecordingTime(prev => prev + 1);
      }, 1000);
    } else {
      setRecordingTime(0);
    }
    return () => clearInterval(interval);
  }, [isRecording]);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  // Initialize audio context for real-time monitoring
  const initializeAudioContext = async (stream) => {
    try {
      const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
      const analyserNode = audioCtx.createAnalyser();
      const source = audioCtx.createMediaStreamSource(stream);

      analyserNode.fftSize = 256;
      analyserNode.smoothingTimeConstant = 0.8;
      source.connect(analyserNode);

      setAudioContext(audioCtx);
      setAnalyser(analyserNode);

      // Start monitoring audio levels
      monitorAudioLevels();
    } catch (error) {
      console.error('Error initializing audio context:', error);
    }
  };

  // Monitor audio levels for voice activity detection
  const monitorAudioLevels = () => {
    if (!analyser) return;

    const bufferLength = analyser.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);

    const checkAudioLevel = () => {
      if (!isRecording) return;

      analyser.getByteFrequencyData(dataArray);

      // Calculate average volume level
      let sum = 0;
      for (let i = 0; i < bufferLength; i++) {
        sum += dataArray[i];
      }
      const average = sum / bufferLength;

      // Voice activity detection threshold (adjust based on testing)
      const threshold = 15; // Adjust this value based on testing
      const isCurrentlySpeaking = average > threshold;

      if (isCurrentlySpeaking !== isUserSpeaking) {
        setIsUserSpeaking(isCurrentlySpeaking);

        if (isCurrentlySpeaking) {
          // User started speaking - clear any silence timer
          if (silenceTimer) {
            clearTimeout(silenceTimer);
            setSilenceTimer(null);
          }
        } else {
          // User stopped speaking - start silence timer
          const timer = setTimeout(() => {
            // User has been silent for the threshold period - stop recording
            if (mediaRecorderRef.current && isRecording) {
              console.log('Auto-stopping recording due to silence detection');
              mediaRecorderRef.current.stop();
              setIsRecording(false);
              setIsListening(false);
            }
          }, silenceThreshold);

          setSilenceTimer(timer);
        }
      }

      // Continue monitoring
      requestAnimationFrame(checkAudioLevel);
    };

    checkAudioLevel();
  };

  // Cleanup audio context
  const cleanupAudioContext = () => {
    if (audioContext && audioContext.state !== 'closed') {
      audioContext.close();
      setAudioContext(null);
      setAnalyser(null);
    }
    if (silenceTimer) {
      clearTimeout(silenceTimer);
      setSilenceTimer(null);
    }
  };

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        audio: isAudioEnabled,
        video: false // We'll use webcam separately for video
      });

      mediaRecorderRef.current = new MediaRecorder(stream, {
        mimeType: 'audio/webm'
      });

      mediaRecorderRef.current.ondataavailable = (event) => {
        if (event.data.size > 0) {
          setRecordedChunks(prev => [...prev, event.data]);
        }
      };

      mediaRecorderRef.current.onstop = () => {
        // Process the recording here
        handleAnswerProcessingModified();
      };

          mediaRecorderRef.current.start();
          setIsRecording(true);
          setIsListening(true);

          // Initialize audio context for real-time monitoring
          initializeAudioContext(stream);

          // Initialize audio context for real-time monitoring
          initializeAudioContext(stream);

          // Auto-stop recording after 30 seconds as fallback
          setTimeout(() => {
            if (mediaRecorderRef.current && isRecording) {
              mediaRecorderRef.current.stop();
              setIsRecording(false);
              setIsListening(false);
              cleanupAudioContext();
            }
          }, 30000);
      setShowTips(false);
    } catch (err) {
      console.error('Error starting recording:', err);
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
      setIsListening(false);
      setIsProcessing(true);

      // Stop all tracks
      mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
    }
  };

  const handleAnswerProcessing = () => {
    // Simulate processing time
    setTimeout(() => {
      setIsProcessing(false);

      // Move to next question or end interview
      if (questionNumber < totalQuestions) {
        setQuestionNumber(prev => prev + 1);
        setCurrentQuestion(mockQuestions[questionNumber]);
        setQuestionStartTime(Date.now());
        setShowTips(true);
      } else {
        setInterviewComplete(true);
        setShowEndDialog(true);
      }
    }, 3000); // 3 second processing simulation
  };

  const handleEndInterview = () => {
    setShowEndDialog(false);
    navigate('/');
  };

  const toggleVideo = () => {
    setIsVideoEnabled(prev => !prev);
  };

  const toggleAudio = () => {
    setIsAudioEnabled(prev => !prev);
  };

  // Text-to-speech functionality with Indian feminine voice
  const speakQuestion = (text) => {
    if (!speechSupported || !text) return;

    // Cancel any ongoing speech
    window.speechSynthesis.cancel();

    const utterance = new SpeechSynthesisUtterance(text);

    // Configure voice settings for Indian feminine sound
    utterance.rate = 0.85; // Slightly slower for Indian accent
    utterance.pitch = 1.2; // Higher pitch for feminine sound
    utterance.volume = 0.9; // Good volume

    // Try to find Indian or feminine voice
    const voices = window.speechSynthesis.getVoices();
    const preferredVoice = voices.find(voice =>
      voice.name.toLowerCase().includes('female') ||
      voice.name.toLowerCase().includes('woman') ||
      voice.name.toLowerCase().includes('girl') ||
      voice.name.toLowerCase().includes('karen') ||
      voice.name.toLowerCase().includes('samantha') ||
      voice.name.toLowerCase().includes('zira') ||
      voice.lang.includes('en-IN') ||
      voice.lang.includes('en-GB') ||
      (voice.lang.startsWith('en-') && !voice.name.toLowerCase().includes('male'))
    ) || voices.find(voice =>
      voice.lang.startsWith('en-')
    );

    if (preferredVoice) {
      utterance.voice = preferredVoice;
    }

    utterance.onstart = () => {
      setIsSpeaking(true);
    };

    utterance.onend = () => {
      setIsSpeaking(false);
      setQuestionSpoken(true);
    };

    utterance.onerror = (event) => {
      console.error('Speech synthesis error:', event);
      setIsSpeaking(false);
    };

    window.speechSynthesis.speak(utterance);
  };

  const replayQuestion = () => {
    speakQuestion(currentQuestion);
  };

  // Auto-speak question when it changes
  useEffect(() => {
    if (currentQuestion && speechSupported && !questionSpoken) {
      // Small delay to ensure UI is updated
      const timer = setTimeout(() => {
        speakQuestion(currentQuestion);
      }, 1000);

      return () => clearTimeout(timer);
    }
  }, [currentQuestion, speechSupported, questionSpoken]);

  // Automatic transition from introduction to questions
  useEffect(() => {
    if (interviewPhase === 'introduction' && questionSpoken && !isProcessing) {
      // After introduction is spoken, automatically transition to questions
      const transitionTimer = setTimeout(() => {
        setInterviewPhase('questions');
        setQuestionNumber(1);
        setCurrentQuestion(`${mockQuestions[0]}`);
        setQuestionStartTime(Date.now());
        setQuestionSpoken(false);
      }, 2000); // 2 second pause after welcome

      return () => clearTimeout(transitionTimer);
    }
  }, [interviewPhase, questionSpoken, isProcessing]);

  // Natural conversation flow with automatic recording
  useEffect(() => {
    if (interviewPhase === 'questions' && !isRecording && !isProcessing && !showMotivation && questionSpoken) {
      // Start automatic recording after question is spoken
      const startAutoRecording = async () => {
        try {
          const stream = await navigator.mediaDevices.getUserMedia({
            audio: isAudioEnabled,
            video: false
          });

          mediaRecorderRef.current = new MediaRecorder(stream, {
            mimeType: 'audio/webm'
          });

          mediaRecorderRef.current.ondataavailable = (event) => {
            if (event.data.size > 0) {
              setRecordedChunks(prev => [...prev, event.data]);
            }
          };

          mediaRecorderRef.current.onstop = () => {
            // Stop all tracks first
            mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
            // Start processing
            setIsProcessing(true);
            handleAnswerProcessingModified();
          };

          mediaRecorderRef.current.start();
          setIsRecording(true);
          setIsListening(true);

          // Initialize audio context for real-time monitoring
          initializeAudioContext(stream);

          // Start response timer - if no response for 15 seconds, give reminder
          setTimeRemaining(15);
          const responseTimer = setInterval(() => {
            setTimeRemaining(prev => {
              if (prev <= 1) {
                clearInterval(responseTimer);
                // Stop current recording and give reminder
                if (mediaRecorderRef.current && isRecording) {
                  mediaRecorderRef.current.stop();
                  setIsRecording(false);
                  setIsListening(false);
                  cleanupAudioContext();
                }
                giveReminder();
                return 0;
              }
              return prev - 1;
            });
          }, 1000);

          setResponseTimer(responseTimer);

        } catch (err) {
          console.error('Error starting automatic recording:', err);
        }
      };

      // Small delay before starting recording
      const timer = setTimeout(startAutoRecording, 2000);
      return () => clearTimeout(timer);
    }
  }, [interviewPhase, isRecording, isProcessing, showMotivation, questionSpoken]);

  // Start interview introduction
  const startInterviewIntroduction = () => {
    setInterviewPhase('introduction');
    setCurrentQuestion("Hello! Welcome to your AI-powered technical interview. I'm excited to speak with you today. Let's begin with our first question.");
    setQuestionStartTime(Date.now());

    // Start automatic transition to questions after speaking
    setTimeout(() => {
      speakQuestion("Hello! Welcome to your AI-powered technical interview. I'm excited to speak with you today. Let's begin with our first question.");
    }, 500);
  };

  // Handle name collection and transition to questions
  const handleNameResponse = () => {
    // Simulate processing the name response
    setIsProcessing(true);
    setTimeout(() => {
      setIsProcessing(false);
      // In real app, extract name from speech response
      // For demo, we'll use a placeholder that gets updated when user speaks
      setUserName('there'); // Will be updated when user responds
      setInterviewPhase('questions');
      setQuestionNumber(1);
      setCurrentQuestion(`Great! Let's begin with our first question. ${mockQuestions[0]}`);
      setQuestionStartTime(Date.now());
      setQuestionSpoken(false);
    }, 2000);
  };

  // Give reminder if user doesn't respond
  const giveReminder = () => {
    setShowMotivation(true);
    setCurrentQuestion(`I notice you haven't started speaking yet. Are you comfortable with this question, or would you prefer to move to the next one? Take your time - I'm here to help you succeed!`);

    // Speak the reminder
    speakQuestion(`I notice you haven't started speaking yet. Are you comfortable with this question, or would you prefer to move to the next one? Take your time - I'm here to help you succeed!`);

    setTimeout(() => {
      setShowMotivation(false);
      // Auto-advance to next question after reminder
      if (questionNumber < totalQuestions) {
        setQuestionNumber(prev => prev + 1);
        const nextQuestion = userName
          ? `Alright, let's try this next question. ${mockQuestions[questionNumber]}`
          : `Alright, let's try this next question. ${mockQuestions[questionNumber]}`;
        setCurrentQuestion(nextQuestion);
        setQuestionStartTime(Date.now());
        setQuestionSpoken(false);
      } else {
        setInterviewComplete(true);
        setShowEndDialog(true);
      }
    }, 6000); // Give more time for reminder
  };

  // Show motivation message and auto-advance
  const showMotivationMessage = () => {
    setShowMotivation(true);
    setCurrentQuestion("That's okay! Take your time. Remember, preparation is key to success. Let's move forward with confidence!");

    setTimeout(() => {
      setShowMotivation(false);
      // Auto-advance to next question
      if (questionNumber < totalQuestions) {
        setQuestionNumber(prev => prev + 1);
        const nextQuestion = userName
          ? `Hi ${userName}! ${mockQuestions[questionNumber]}`
          : mockQuestions[questionNumber];
        setCurrentQuestion(nextQuestion);
        setQuestionStartTime(Date.now());
        setQuestionSpoken(false);
      } else {
        setInterviewComplete(true);
        setShowEndDialog(true);
      }
    }, 4000); // Show motivation for 4 seconds
  };

  // Modified answer processing to handle introduction phase
  const handleAnswerProcessingModified = () => {
    // Simulate processing time
    setTimeout(() => {
      setIsProcessing(false);

      if (interviewPhase === 'introduction') {
        // Process name and transition to questions
        handleNameResponse();
      } else {
        // Normal question processing
        if (questionNumber < totalQuestions) {
          const nextQuestionNumber = questionNumber + 1;
          setQuestionNumber(nextQuestionNumber);
          const nextQuestion = userName
            ? `Hi ${userName}! ${mockQuestions[nextQuestionNumber]}`
            : mockQuestions[nextQuestionNumber];
          setCurrentQuestion(nextQuestion);
          setQuestionStartTime(Date.now());
          setQuestionSpoken(false);
        } else {
          setInterviewComplete(true);
          setShowEndDialog(true);
        }
      }
    }, 3000); // 3 second processing simulation
  };

  const webcamConstraints = {
    width: 640,
    height: 480,
    facingMode: 'user'
  };

  const questionTimeElapsed = questionStartTime ? Math.floor((Date.now() - questionStartTime) / 1000) : 0;

  return (
    <Box
      sx={{
        minHeight: '100vh',
        bgcolor: 'grey.900',
        color: 'white',
        py: 2
      }}
    >
      <Container maxWidth="xl">
        {/* Header */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3, px: 2 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <Avatar sx={{ bgcolor: 'primary.main', width: 40, height: 40 }}>
              <AssessmentIcon />
            </Avatar>
            <Box>
              <Typography variant="h5" sx={{ fontWeight: 600 }}>
                Live Interview Session
              </Typography>
              <Typography variant="body2" sx={{ color: 'grey.400' }}>
                AI-Powered Technical Interview
              </Typography>
            </Box>
          </Box>

          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            {interviewPhase === 'questions' && (
              <Chip
                label={`Question ${questionNumber}/${totalQuestions}`}
                color="primary"
                variant="outlined"
                sx={{ borderColor: 'primary.main', color: 'primary.main' }}
              />
            )}
            <Chip
              icon={<TimerIcon />}
              label={formatTime(questionTimeElapsed)}
              variant="outlined"
              sx={{ borderColor: 'grey.600', color: 'grey.300' }}
            />
          </Box>
        </Box>

        <Box sx={{ display: 'flex', gap: 3, flexDirection: { xs: 'column', lg: 'row' } }}>
          {/* AI Interviewer Section */}
          <Card
            sx={{
              flex: 1,
              minHeight: 500,
              bgcolor: 'grey.800',
              border: '2px solid',
              borderColor: isListening ? 'error.main' : 'grey.700',
              transition: 'all 0.3s ease'
            }}
          >
            <CardContent sx={{ height: '100%', p: 4 }}>
              {/* AI Avatar */}
              <Box sx={{ textAlign: 'center', mb: 4 }}>
                <Badge
                  overlap="circular"
                  anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
                  badgeContent={
                    isListening ? (
                      <Box
                        sx={{
                          bgcolor: 'error.main',
                          borderRadius: '50%',
                          width: 20,
                          height: 20,
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center',
                          animation: 'pulse 1s infinite'
                        }}
                      >
                        <MicIcon sx={{ fontSize: 12, color: 'white' }} />
                      </Box>
                    ) : (
                      <CheckCircleIcon sx={{ bgcolor: 'success.main', borderRadius: '50%', p: 0.5 }} />
                    )
                  }
                >
                  <Avatar
                    sx={{
                      width: 120,
                      height: 120,
                      bgcolor: 'primary.main',
                      mx: 'auto',
                      boxShadow: '0 8px 32px rgba(37, 99, 235, 0.3)',
                      border: '4px solid',
                      borderColor: isListening ? 'error.main' : 'primary.main'
                    }}
                  >
                    <AIIcon sx={{ fontSize: 60 }} />
                  </Avatar>
                </Badge>

                <Typography variant="h5" sx={{ mt: 2, fontWeight: 600 }}>
                  AI Interviewer
                </Typography>
                <Typography variant="body2" sx={{ color: 'grey.400', mb: 2 }}>
                  Advanced AI Interview Specialist
                </Typography>

                {/* Status Indicator */}
                <Box sx={{ display: 'flex', justifyContent: 'center', gap: 1, mb: 3, flexWrap: 'wrap' }}>
                  <Chip
                    size="small"
                    icon={isListening ? <MicIcon /> : <MicOffIcon />}
                    label={isListening ? "Active Listening" : "Ready"}
                    color={isListening ? "error" : "success"}
                    variant={isListening ? "filled" : "outlined"}
                    sx={{
                      '& .MuiChip-icon': { fontSize: 16 }
                    }}
                  />
                  {isSpeaking && (
                    <Chip
                      size="small"
                      icon={<VolumeUpIcon sx={{ animation: 'pulse 1s infinite' }} />}
                      label="AI Speaking"
                      color="info"
                      variant="filled"
                      sx={{
                        animation: 'pulse 1s infinite',
                        '& .MuiChip-icon': { fontSize: 16 }
                      }}
                    />
                  )}
                  {isUserSpeaking && isRecording && (
                    <Chip
                      size="small"
                      icon={<MicIcon sx={{ animation: 'pulse 1s infinite' }} />}
                      label="You Speaking"
                      color="primary"
                      variant="filled"
                      sx={{
                        animation: 'pulse 1s infinite',
                        '& .MuiChip-icon': { fontSize: 16 }
                      }}
                    />
                  )}
                  {isProcessing && (
                    <Chip
                      size="small"
                      icon={<CircularProgress size={14} />}
                      label="Analyzing"
                      color="warning"
                      variant="filled"
                    />
                  )}
                  {!speechSupported && (
                    <Chip
                      size="small"
                      icon={<VolumeOffIcon />}
                      label="Voice Unavailable"
                      color="warning"
                      variant="outlined"
                    />
                  )}
                </Box>
              </Box>

              {/* Question Display */}
              <Box sx={{ mb: 3 }}>
                <Box
                  sx={{
                    p: 3,
                    bgcolor: 'grey.900',
                    borderRadius: 3,
                    border: '1px solid',
                    borderColor: 'grey.700',
                    minHeight: 120,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    mb: 2
                  }}
                >
                  <Typography
                    variant="h6"
                    align="center"
                    sx={{
                      lineHeight: 1.6,
                      fontWeight: 400,
                      color: isListening ? 'primary.light' : 'grey.200'
                    }}
                  >
                    {currentQuestion || "Preparing your question..."}
                  </Typography>
                </Box>

                {/* Voice Controls */}
                {speechSupported && currentQuestion && (
                  <Box sx={{ display: 'flex', justifyContent: 'center', gap: 1 }}>
                    <Button
                      variant="outlined"
                      size="small"
                      startIcon={
                        isSpeaking ? (
                          <VolumeUpIcon sx={{ animation: 'pulse 1s infinite' }} />
                        ) : (
                          <VolumeUpIcon />
                        )
                      }
                      onClick={replayQuestion}
                      disabled={isSpeaking}
                      sx={{
                        borderColor: 'primary.main',
                        color: 'primary.main',
                        '&:hover': {
                          borderColor: 'primary.light',
                          bgcolor: 'primary.main',
                          color: 'white'
                        },
                        '&:disabled': {
                          borderColor: 'grey.600',
                          color: 'grey.600'
                        }
                      }}
                    >
                      {isSpeaking ? 'Speaking...' : 'Replay Question'}
                    </Button>
                  </Box>
                )}

                {!speechSupported && (
                  <Alert severity="warning" sx={{ borderRadius: 2 }}>
                    <Typography variant="body2">
                      Voice synthesis is not supported in your browser. Questions will be displayed in text only.
                    </Typography>
                  </Alert>
                )}
              </Box>

              {/* Recording Timer */}
              {isRecording && (
                <Box sx={{ textAlign: 'center', mb: 2 }}>
                  <Typography variant="body2" sx={{ color: 'error.main', fontWeight: 500 }}>
                     Recording: {formatTime(recordingTime)}
                  </Typography>
                </Box>
              )}

              {/* Tips and Countdown */}
              {showTips && !isRecording && interviewPhase === 'questions' && (
                <Fade in={showTips}>
                  <Alert
                    severity={timeRemaining <= 3 ? "warning" : "info"}
                    sx={{
                      bgcolor: timeRemaining <= 3 ? 'warning.dark' : 'info.dark',
                      color: 'white',
                      '& .MuiAlert-icon': {
                        color: timeRemaining <= 3 ? 'warning.light' : 'info.light'
                      }
                    }}
                  >
                    <Typography variant="body2">
                      <strong> {timeRemaining}s remaining:</strong> Take a moment to think about your answer.
                      Click "Start Recording" when you're ready to begin speaking.
                      {timeRemaining <= 3 && " Don't worry - we'll move forward with encouragement!"}
                    </Typography>
                  </Alert>
                </Fade>
              )}

              {/* Motivation Message */}
              {showMotivation && (
                <Fade in={showMotivation}>
                  <Alert
                    severity="success"
                    sx={{
                      bgcolor: 'success.dark',
                      color: 'white',
                      '& .MuiAlert-icon': { color: 'success.light' }
                    }}
                  >
                    <Typography variant="body2">
                      <strong> Keep going!</strong> That's okay! Take your time. Remember, preparation is key to success.
                      Let's move forward with confidence!
                    </Typography>
                  </Alert>
                </Fade>
              )}
            </CardContent>
          </Card>

          {/* User Video Section */}
          <Card
            sx={{
              flex: 1,
              minHeight: 500,
              bgcolor: 'grey.800',
              border: '2px solid',
              borderColor: 'grey.700'
            }}
          >
            <CardContent sx={{ height: '100%', p: 4 }}>
              {/* Video Header */}
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                <Typography variant="h6" sx={{ fontWeight: 600 }}>
                  Your Camera Feed
                </Typography>

                <Box sx={{ display: 'flex', gap: 1 }}>
                  <Tooltip title={isVideoEnabled ? "Disable Video" : "Enable Video"}>
                    <IconButton
                      onClick={toggleVideo}
                      sx={{
                        bgcolor: isVideoEnabled ? 'success.main' : 'grey.600',
                        color: 'white',
                        '&:hover': {
                          bgcolor: isVideoEnabled ? 'success.dark' : 'grey.500'
                        }
                      }}
                    >
                      {isVideoEnabled ? <VideocamIcon /> : <VideocamOffIcon />}
                    </IconButton>
                  </Tooltip>

                  <Tooltip title={isAudioEnabled ? "Disable Audio" : "Enable Audio"}>
                    <IconButton
                      onClick={toggleAudio}
                      sx={{
                        bgcolor: isAudioEnabled ? 'success.main' : 'grey.600',
                        color: 'white',
                        '&:hover': {
                          bgcolor: isAudioEnabled ? 'success.dark' : 'grey.500'
                        }
                      }}
                    >
                      {isAudioEnabled ? <VolumeUpIcon /> : <VolumeOffIcon />}
                    </IconButton>
                  </Tooltip>
                </Box>
              </Box>

              {/* Video Feed */}
              <Box
                sx={{
                  flex: 1,
                  display: 'flex',
                  justifyContent: 'center',
                  alignItems: 'center',
                  mb: 3,
                  minHeight: 300
                }}
              >
                {isVideoEnabled ? (
                  <Webcam
                    ref={webcamRef}
                    audio={false}
                    videoConstraints={webcamConstraints}
                    style={{
                      width: '100%',
                      maxWidth: '400px',
                      height: '300px',
                      borderRadius: '12px',
                      border: `3px solid ${isRecording ? '#ef4444' : '#374151'}`,
                      objectFit: 'cover'
                    }}
                  />
                ) : (
                  <Box
                    sx={{
                      width: '100%',
                      maxWidth: '400px',
                      height: '300px',
                      bgcolor: 'grey.700',
                      borderRadius: '12px',
                      border: '3px solid #374151',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center'
                    }}
                  >
                    <Box sx={{ textAlign: 'center' }}>
                      <VideocamOffIcon sx={{ fontSize: 48, color: 'grey.500', mb: 1 }} />
                      <Typography variant="body2" color="grey.400">
                        Video Disabled
                      </Typography>
                    </Box>
                  </Box>
                )}
              </Box>

              {/* Automatic Interview Status */}
              <Box sx={{ textAlign: 'center' }}>
                {isRecording && (
                  <Box sx={{ mb: 2 }}>
                    <Typography variant="body2" sx={{ color: 'error.main', fontWeight: 500, mb: 1 }}>
                       Listening... Please share your thoughts
                    </Typography>
                    <Typography variant="body2" sx={{ color: 'grey.400' }}>
                      Recording: {formatTime(recordingTime)}
                    </Typography>
                  </Box>
                )}

                {interviewPhase === 'questions' && !isRecording && !isProcessing && !showMotivation && (
                  <Box sx={{ mb: 2 }}>
                    <Typography variant="body2" sx={{ color: 'info.main', fontWeight: 500, mb: 1 }}>
                       Thinking time... Take a moment to prepare
                    </Typography>
                    <Typography variant="body2" sx={{ color: 'grey.400' }}>
                      Recording will start automatically in a moment
                    </Typography>
                  </Box>
                )}

                {showMotivation && (
                  <Box sx={{ mb: 2 }}>
                    <Typography variant="body2" sx={{ color: 'warning.main', fontWeight: 500 }}>
                       Checking in with you...
                    </Typography>
                  </Box>
                )}

                {!isAudioEnabled && (
                  <Typography variant="body2" sx={{ color: 'warning.main', mt: 1 }}>
                     Audio is disabled. Please enable audio for the interview.
                  </Typography>
                )}
              </Box>

              {/* Processing Indicator */}
              {isProcessing && (
                <Box sx={{ mt: 3 }}>
                  <Typography variant="body2" align="center" sx={{ mb: 2, color: 'warning.main' }}>
                    Analyzing your response with AI...
                  </Typography>
                  <LinearProgress
                    sx={{
                      height: 8,
                      borderRadius: 4,
                      bgcolor: 'grey.700',
                      '& .MuiLinearProgress-bar': {
                        bgcolor: 'warning.main',
                        borderRadius: 4
                      }
                    }}
                  />
                </Box>
              )}
            </CardContent>
          </Card>
        </Box>

        {/* Progress Bar - Only show during questions phase */}
        {interviewPhase === 'questions' && (
          <Box sx={{ mt: 4, px: 2 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 1 }}>
              <Typography variant="body2" sx={{ color: 'grey.400' }}>
                Interview Progress
              </Typography>
              <Typography variant="body2" sx={{ color: 'grey.400' }}>
                {questionNumber}/{totalQuestions} Questions
              </Typography>
            </Box>
            <LinearProgress
              variant="determinate"
              value={(questionNumber / totalQuestions) * 100}
              sx={{
                height: 10,
                borderRadius: 5,
                bgcolor: 'grey.700',
                '& .MuiLinearProgress-bar': {
                  background: 'linear-gradient(45deg, #2563eb, #7c3aed)',
                  borderRadius: 5
                }
              }}
            />
          </Box>
        )}

        {/* Interview Complete Dialog */}
        <Dialog
          open={showEndDialog}
          onClose={() => setShowEndDialog(false)}
          PaperProps={{
            sx: {
              bgcolor: 'grey.800',
              color: 'white',
              borderRadius: 3,
              border: '1px solid #374151'
            }
          }}
        >
          <DialogTitle sx={{ textAlign: 'center', pb: 1 }}>
            <CheckCircleIcon sx={{ fontSize: 48, color: 'success.main', mb: 1 }} />
            <Typography variant="h4" sx={{ fontWeight: 600 }}>
              Interview Complete!
            </Typography>
          </DialogTitle>
          <DialogContent sx={{ textAlign: 'center', pb: 3 }}>
            <Typography variant="h6" sx={{ mb: 2, color: 'grey.300' }}>
              Congratulations on completing your AI-powered interview session!
            </Typography>
            <Typography variant="body1" sx={{ color: 'grey.400', lineHeight: 1.6 }}>
              Your responses are being analyzed by our advanced AI system.
              A comprehensive feedback report with detailed insights will be generated shortly.
            </Typography>
          </DialogContent>
          <DialogActions sx={{ justifyContent: 'center', pb: 3 }}>
            <Button
              onClick={handleEndInterview}
              variant="contained"
              size="large"
              sx={{
                px: 4,
                py: 1.5,
                fontSize: '1.1rem',
                fontWeight: 600,
                borderRadius: 3,
                background: 'linear-gradient(45deg, #059669, #10b981)',
                '&:hover': {
                  background: 'linear-gradient(45deg, #047857, #059669)',
                }
              }}
            >
              View Results & Feedback
            </Button>
          </DialogActions>
        </Dialog>
      </Container>
    </Box>
  );
};

export default Interview;
