import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  Box,
  Button,
  Alert,
  Card,
  CardContent,
  Chip,
  LinearProgress,
  Avatar,
  Fade,
  CircularProgress,
  Tooltip,
  IconButton,
  Collapse
} from '@mui/material';
import {
  Mic as MicIcon,
  MicOff as MicOffIcon,
  Videocam as CameraIcon,
  VideocamOff as VideocamOffIcon,
  ScreenShare as ScreenIcon,
  CheckCircle as CheckIcon,
  Error as ErrorIcon,
  Warning as WarningIcon,
  Info as InfoIcon,
  Refresh as RefreshIcon,
  ExpandMore as ExpandMoreIcon,
  ExpandLess as ExpandLessIcon
} from '@mui/icons-material';

const Permissions = () => {
  const navigate = useNavigate();
  const [permissions, setPermissions] = useState({
    microphone: { status: 'unknown', granted: false, testing: false },
    camera: { status: 'unknown', granted: false, testing: false },
    screen: { status: 'unknown', granted: false, testing: false }
  });
  const [isChecking, setIsChecking] = useState(false);
  const [error, setError] = useState('');
  const [expandedCard, setExpandedCard] = useState(null);
  const [testResults, setTestResults] = useState({
    microphone: null,
    camera: null,
    screen: null
  });
  const videoRef = useRef(null);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const streamRef = useRef(null);

  useEffect(() => {
    return () => {
      // Cleanup streams and audio context on unmount
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }
      if (audioContextRef.current) {
        audioContextRef.current.close();
      }
    };
  }, []);

  const checkMicrophonePermission = async () => {
    setPermissions(prev => ({
      ...prev,
      microphone: { ...prev.microphone, testing: true }
    }));

    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        audio: { echoCancellation: true, noiseSuppression: true }
      });

      // Test audio levels
      const audioContext = new AudioContext();
      const analyser = audioContext.createAnalyser();
      const microphone = audioContext.createMediaStreamSource(stream);
      microphone.connect(analyser);
      analyser.fftSize = 256;

      audioContextRef.current = audioContext;
      analyserRef.current = analyser;

      // Monitor audio levels for 2 seconds
      let maxLevel = 0;
      const monitorAudio = () => {
        const bufferLength = analyser.frequencyBinCount;
        const dataArray = new Uint8Array(bufferLength);
        analyser.getByteFrequencyData(dataArray);

        const average = dataArray.reduce((a, b) => a + b) / bufferLength;
        maxLevel = Math.max(maxLevel, average);
      };

      const interval = setInterval(monitorAudio, 100);
      await new Promise(resolve => setTimeout(resolve, 2000));
      clearInterval(interval);

      stream.getTracks().forEach(track => track.stop());

      const audioWorking = maxLevel > 10; // Basic threshold for audio detection

      setPermissions(prev => ({
        ...prev,
        microphone: {
          status: 'granted',
          granted: true,
          testing: false
        }
      }));

      setTestResults(prev => ({
        ...prev,
        microphone: {
          working: audioWorking,
          level: maxLevel,
          message: audioWorking ? 'Microphone is working properly' : 'Microphone detected but may need adjustment'
        }
      }));

      return true;
    } catch (err) {
      console.error('Microphone permission denied:', err);
      setPermissions(prev => ({
        ...prev,
        microphone: {
          status: 'denied',
          granted: false,
          testing: false
        }
      }));

      setTestResults(prev => ({
        ...prev,
        microphone: {
          working: false,
          error: err.name === 'NotAllowedError' ? 'Permission denied' : 'Microphone not found'
        }
      }));

      return false;
    }
  };

  const checkCameraPermission = async () => {
    setPermissions(prev => ({
      ...prev,
      camera: { ...prev.camera, testing: true }
    }));

    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: {
          width: { ideal: 1280 },
          height: { ideal: 720 },
          facingMode: 'user'
        }
      });

      streamRef.current = stream;
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }

      // Test video by checking if video dimensions are reasonable
      await new Promise(resolve => {
        if (videoRef.current) {
          videoRef.current.onloadedmetadata = resolve;
        } else {
          setTimeout(resolve, 1000);
        }
      });

      const videoWidth = videoRef.current?.videoWidth || 0;
      const videoHeight = videoRef.current?.videoHeight || 0;
      const videoWorking = videoWidth > 320 && videoHeight > 240;

      setPermissions(prev => ({
        ...prev,
        camera: {
          status: 'granted',
          granted: true,
          testing: false
        }
      }));

      setTestResults(prev => ({
        ...prev,
        camera: {
          working: videoWorking,
          resolution: `${videoWidth}x${videoHeight}`,
          message: videoWorking ? 'Camera is working properly' : 'Camera resolution may be low'
        }
      }));

      return true;
    } catch (err) {
      console.error('Camera permission denied:', err);
      setPermissions(prev => ({
        ...prev,
        camera: {
          status: 'denied',
          granted: false,
          testing: false
        }
      }));

      setTestResults(prev => ({
        ...prev,
        camera: {
          working: false,
          error: err.name === 'NotAllowedError' ? 'Permission denied' : 'Camera not found'
        }
      }));

      return false;
    }
  };

  const checkScreenPermission = async () => {
    setPermissions(prev => ({
      ...prev,
      screen: { ...prev.screen, testing: true }
    }));

    try {
      // Check if screen sharing is supported
      if (!navigator.mediaDevices.getDisplayMedia) {
        setPermissions(prev => ({
          ...prev,
          screen: {
            status: 'unsupported',
            granted: false,
            testing: false
          }
        }));

        setTestResults(prev => ({
          ...prev,
          screen: {
            working: false,
            error: 'Screen sharing not supported in this browser'
          }
        }));

        return false;
      }

      // For screen sharing, we'll just check if the API is available
      // Actual permission will be requested during the interview
      setPermissions(prev => ({
        ...prev,
        screen: {
          status: 'available',
          granted: true,
          testing: false
        }
      }));

      setTestResults(prev => ({
        ...prev,
        screen: {
          working: true,
          message: 'Screen sharing is available and will be requested when needed'
        }
      }));

      return true;
    } catch (err) {
      console.error('Screen sharing not supported:', err);
      setPermissions(prev => ({
        ...prev,
        screen: {
          status: 'unsupported',
          granted: false,
          testing: false
        }
      }));

      setTestResults(prev => ({
        ...prev,
        screen: {
          working: false,
          error: 'Screen sharing not supported'
        }
      }));

      return false;
    }
  };

  const checkAllPermissions = async () => {
    setIsChecking(true);
    setError('');
    setTestResults({ microphone: null, camera: null, screen: null });

    const micResult = await checkMicrophonePermission();
    const cameraResult = await checkCameraPermission();
    const screenResult = await checkScreenPermission();

    setIsChecking(false);

    if (!micResult || !cameraResult) {
      setError('Microphone and camera permissions are required for the interview. Please allow access when prompted and try again.');
    }
  };

  const handleContinue = () => {
    const allGranted = permissions.microphone.granted &&
                      permissions.camera.granted &&
                      permissions.screen.granted;

    if (!allGranted) {
      setError('Please ensure all required permissions are granted before continuing.');
      return;
    }

    // Stop camera stream before navigating
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
    }

    navigate('/instructions');
  };

  const getStatusIcon = (status, testing = false) => {
    if (testing) return <CircularProgress size={20} />;

    switch (status) {
      case 'granted':
      case 'available':
        return <CheckIcon color="success" />;
      case 'denied':
        return <ErrorIcon color="error" />;
      case 'unsupported':
        return <WarningIcon color="warning" />;
      default:
        return <InfoIcon color="action" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'granted':
      case 'available':
        return 'success';
      case 'denied':
        return 'error';
      case 'unsupported':
        return 'warning';
      default:
        return 'default';
    }
  };

  const getStatusText = (status, testing = false) => {
    if (testing) return 'Testing...';

    switch (status) {
      case 'granted':
        return 'Granted';
      case 'available':
        return 'Available';
      case 'denied':
        return 'Denied';
      case 'unsupported':
        return 'Not Supported';
      default:
        return 'Unknown';
    }
  };

  const permissionCards = [
    {
      id: 'microphone',
      title: 'Microphone Access',
      description: 'Required for audio recording during the interview',
      icon: MicIcon,
      required: true,
      tips: [
        'Ensure your microphone is not muted',
        'Test audio levels in a quiet environment',
        'Use a good quality microphone if possible'
      ]
    },
    {
      id: 'camera',
      title: 'Camera Access',
      description: 'Required for video recording during the interview',
      icon: CameraIcon,
      required: true,
      tips: [
        'Ensure good lighting on your face',
        'Position camera at eye level',
        'Frame yourself properly in the shot'
      ]
    },
    {
      id: 'screen',
      title: 'Screen Recording',
      description: 'Optional for sharing your screen during coding questions',
      icon: ScreenIcon,
      required: false,
      tips: [
        'Close unnecessary applications',
        'Ensure screen content is appropriate',
        'Test screen sharing before interview'
      ]
    }
  ];

  return (
    <Box
      sx={{
        minHeight: '100vh',
        bgcolor: 'grey.50',
        py: 4
      }}
    >
      <Container maxWidth="lg">
        <Paper
          elevation={8}
          sx={{
            p: 4,
            background: 'linear-gradient(145deg, #ffffff 0%, #f8fafc 100%)'
          }}
        >
          {/* Header */}
          <Box sx={{ textAlign: 'center', mb: 4 }}>
            <Typography
              variant="h4"
              sx={{
                fontWeight: 700,
                background: 'linear-gradient(45deg, #2563eb, #7c3aed)',
                backgroundClip: 'text',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                mb: 1
              }}
            >
              System Check
            </Typography>
            <Typography variant="h6" color="text.secondary">
              Preparing your environment for the interview
            </Typography>
          </Box>

          {/* Error Alert */}
          {error && (
            <Fade in={true}>
              <Alert severity="error" sx={{ mb: 3, borderRadius: 2 }}>
                {error}
              </Alert>
            </Fade>
          )}

          {/* Permission Cards */}
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3, mb: 4 }}>
            {permissionCards.map((card) => {
              const Icon = card.icon;
              const permission = permissions[card.id];
              const testResult = testResults[card.id];
              const isExpanded = expandedCard === card.id;

              return (
                <Fade in={true} key={card.id} timeout={500}>
                  <Card
                    sx={{
                      border: `2px solid ${permission.granted ? '#10b981' : permission.status === 'denied' ? '#ef4444' : '#e5e7eb'}`,
                      transition: 'all 0.3s ease',
                      '&:hover': {
                        boxShadow: 6,
                        transform: 'translateY(-2px)'
                      }
                    }}
                  >
                    <CardContent sx={{ p: 3 }}>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 2 }}>
                        <Avatar
                          sx={{
                            bgcolor: permission.granted ? 'success.main' : permission.status === 'denied' ? 'error.main' : 'primary.main',
                            width: 50,
                            height: 50
                          }}
                        >
                          {permission.testing ? (
                            <CircularProgress size={24} color="inherit" />
                          ) : (
                            <Icon sx={{ fontSize: 24 }} />
                          )}
                        </Avatar>

                        <Box sx={{ flexGrow: 1 }}>
                          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 0.5 }}>
                            <Typography variant="h6">
                              {card.title}
                              {card.required && <span style={{ color: 'red', marginLeft: 4 }}>*</span>}
                            </Typography>
                            <Chip
                              size="small"
                              icon={getStatusIcon(permission.status, permission.testing)}
                              label={getStatusText(permission.status, permission.testing)}
                              color={getStatusColor(permission.status)}
                              variant={permission.granted ? "filled" : "outlined"}
                            />
                          </Box>
                          <Typography variant="body2" color="text.secondary">
                            {card.description}
                          </Typography>
                        </Box>

                        <Tooltip title={isExpanded ? "Hide details" : "Show details"}>
                          <IconButton
                            onClick={() => setExpandedCard(isExpanded ? null : card.id)}
                            sx={{
                              transform: isExpanded ? 'rotate(180deg)' : 'rotate(0deg)',
                              transition: 'transform 0.3s ease'
                            }}
                          >
                            <ExpandMoreIcon />
                          </IconButton>
                        </Tooltip>
                      </Box>

                      {/* Test Results */}
                      {testResult && (
                        <Box sx={{ mb: 2 }}>
                          {testResult.working ? (
                            <Alert severity="success" sx={{ borderRadius: 2 }}>
                              <Typography variant="body2">
                                <strong> {testResult.message}</strong>
                                {testResult.resolution && (
                                  <span>  Resolution: {testResult.resolution}</span>
                                )}
                                {testResult.level && (
                                  <span>  Audio Level: {Math.round(testResult.level)}</span>
                                )}
                              </Typography>
                            </Alert>
                          ) : (
                            <Alert severity="error" sx={{ borderRadius: 2 }}>
                              <Typography variant="body2">
                                <strong> {testResult.error || 'Test failed'}</strong>
                              </Typography>
                            </Alert>
                          )}
                        </Box>
                      )}

                      {/* Camera Preview */}
                      {card.id === 'camera' && permission.granted && (
                        <Box sx={{ mt: 2, textAlign: 'center' }}>
                          <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                            Camera Preview
                          </Typography>
                          <video
                            ref={videoRef}
                            autoPlay
                            muted
                            style={{
                              width: '100%',
                              maxWidth: '300px',
                              height: '200px',
                              borderRadius: '12px',
                              border: '2px solid #e5e7eb',
                              objectFit: 'cover'
                            }}
                          />
                        </Box>
                      )}

                      {/* Expandable Tips */}
                      <Collapse in={isExpanded}>
                        <Box sx={{ mt: 2, pt: 2, borderTop: '1px solid #e5e7eb' }}>
                          <Typography variant="subtitle2" gutterBottom sx={{ color: 'primary.main' }}>
                             Tips for best results:
                          </Typography>
                          <Box component="ul" sx={{ pl: 2, m: 0 }}>
                            {card.tips.map((tip, index) => (
                              <Typography
                                key={index}
                                component="li"
                                variant="body2"
                                color="text.secondary"
                                sx={{ mb: 0.5 }}
                              >
                                {tip}
                              </Typography>
                            ))}
                          </Box>
                        </Box>
                      </Collapse>
                    </CardContent>
                  </Card>
                </Fade>
              );
            })}
          </Box>

          {/* Progress and Actions */}
          {isChecking && (
            <Box sx={{ mb: 3 }}>
              <Typography variant="body2" sx={{ mb: 1, textAlign: 'center' }}>
                Testing your system capabilities...
              </Typography>
              <LinearProgress sx={{ borderRadius: 2, height: 8 }} />
            </Box>
          )}

          <Box sx={{ display: 'flex', gap: 2 }}>
            <Button
              variant="outlined"
              onClick={checkAllPermissions}
              disabled={isChecking}
              startIcon={<RefreshIcon />}
              sx={{
                flex: 1,
                py: 1.5,
                borderRadius: 3
              }}
            >
              {isChecking ? 'Testing...' : 'Test All Permissions'}
            </Button>

            <Button
              variant="contained"
              onClick={handleContinue}
              disabled={!permissions.microphone.granted || !permissions.camera.granted || isChecking}
              sx={{
                flex: 1,
                py: 1.5,
                borderRadius: 3,
                background: 'linear-gradient(45deg, #2563eb, #7c3aed)',
                '&:hover': {
                  background: 'linear-gradient(45deg, #1d4ed8, #5b21b6)',
                }
              }}
            >
              Continue to Instructions
            </Button>
          </Box>

          {/* Help Text */}
          <Box sx={{ mt: 3, textAlign: 'center' }}>
            <Typography variant="body2" color="text.secondary">
              Having trouble? Make sure to allow permissions when prompted by your browser.
              You can also check your browser settings to manage camera and microphone permissions.
            </Typography>
          </Box>
        </Paper>
      </Container>
    </Box>
  );
};

export default Permissions;
