import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  Box,
  Button,
  Alert,
  Card,
  CardContent,
  Chip,
  LinearProgress
} from '@mui/material';
import {
  Mic as MicIcon,
  Videocam as CameraIcon,
  ScreenShare as ScreenIcon,
  CheckCircle as CheckIcon,
  Error as ErrorIcon
} from '@mui/icons-material';

const Permissions = () => {
  const navigate = useNavigate();
  const [permissions, setPermissions] = useState({
    microphone: { status: 'unknown', granted: false },
    camera: { status: 'unknown', granted: false },
    screen: { status: 'unknown', granted: false }
  });
  const [isChecking, setIsChecking] = useState(false);
  const [error, setError] = useState('');
  const videoRef = useRef(null);
  const streamRef = useRef(null);

  useEffect(() => {
    return () => {
      // Cleanup streams on unmount
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }
    };
  }, []);

  const checkMicrophonePermission = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      stream.getTracks().forEach(track => track.stop()); // Stop immediately
      setPermissions(prev => ({
        ...prev,
        microphone: { status: 'granted', granted: true }
      }));
      return true;
    } catch (err) {
      console.error('Microphone permission denied:', err);
      setPermissions(prev => ({
        ...prev,
        microphone: { status: 'denied', granted: false }
      }));
      return false;
    }
  };

  const checkCameraPermission = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      streamRef.current = stream;
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
      setPermissions(prev => ({
        ...prev,
        camera: { status: 'granted', granted: true }
      }));
      return true;
    } catch (err) {
      console.error('Camera permission denied:', err);
      setPermissions(prev => ({
        ...prev,
        camera: { status: 'denied', granted: false }
      }));
      return false;
    }
  };

  const checkScreenPermission = async () => {
    try {
      // Check if screen sharing is supported
      if (!navigator.mediaDevices.getDisplayMedia) {
        setPermissions(prev => ({
          ...prev,
          screen: { status: 'unsupported', granted: false }
        }));
        return false;
      }

      // For screen sharing, we'll just check if the API is available
      // Actual permission will be requested during the interview
      setPermissions(prev => ({
        ...prev,
        screen: { status: 'available', granted: true }
      }));
      return true;
    } catch (err) {
      console.error('Screen sharing not supported:', err);
      setPermissions(prev => ({
        ...prev,
        screen: { status: 'unsupported', granted: false }
      }));
      return false;
    }
  };

  const checkAllPermissions = async () => {
    setIsChecking(true);
    setError('');

    const micResult = await checkMicrophonePermission();
    const cameraResult = await checkCameraPermission();
    await checkScreenPermission();

    setIsChecking(false);

    if (!micResult || !cameraResult) {
      setError('Microphone and camera permissions are required for the interview. Please allow access and try again.');
    }
  };

  const handleContinue = () => {
    const allGranted = permissions.microphone.granted &&
                      permissions.camera.granted &&
                      permissions.screen.granted;

    if (!allGranted) {
      setError('Please check all permissions before continuing.');
      return;
    }

    // Stop camera stream before navigating
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
    }

    navigate('/instructions');
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'granted':
      case 'available':
        return <CheckIcon color="success" />;
      case 'denied':
        return <ErrorIcon color="error" />;
      case 'unsupported':
        return <ErrorIcon color="warning" />;
      default:
        return null;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'granted':
      case 'available':
        return 'success';
      case 'denied':
        return 'error';
      case 'unsupported':
        return 'warning';
      default:
        return 'default';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'granted':
        return 'Granted';
      case 'available':
        return 'Available';
      case 'denied':
        return 'Denied';
      case 'unsupported':
        return 'Not Supported';
      default:
        return 'Unknown';
    }
  };

  return (
    <Container component="main" maxWidth="md" sx={{ mt: 4 }}>
      <Paper elevation={3} sx={{ p: 4 }}>
        <Typography component="h1" variant="h4" align="center" gutterBottom>
          Permission Check
        </Typography>
        <Typography variant="subtitle1" align="center" color="text.secondary" sx={{ mb: 3 }}>
          We need access to your microphone, camera, and screen for the interview
        </Typography>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3, mb: 3 }}>
          {/* Microphone Permission */}
          <Card>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                <MicIcon color="primary" />
                <Box sx={{ flexGrow: 1 }}>
                  <Typography variant="h6">Microphone Access</Typography>
                  <Typography variant="body2" color="text.secondary">
                    Required for audio recording during the interview
                  </Typography>
                </Box>
                <Chip
                  icon={getStatusIcon(permissions.microphone.status)}
                  label={getStatusText(permissions.microphone.status)}
                  color={getStatusColor(permissions.microphone.status)}
                />
              </Box>
            </CardContent>
          </Card>

          {/* Camera Permission */}
          <Card>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                <CameraIcon color="primary" />
                <Box sx={{ flexGrow: 1 }}>
                  <Typography variant="h6">Camera Access</Typography>
                  <Typography variant="body2" color="text.secondary">
                    Required for video recording during the interview
                  </Typography>
                </Box>
                <Chip
                  icon={getStatusIcon(permissions.camera.status)}
                  label={getStatusText(permissions.camera.status)}
                  color={getStatusColor(permissions.camera.status)}
                />
              </Box>
              {permissions.camera.granted && (
                <Box sx={{ mt: 2 }}>
                  <video
                    ref={videoRef}
                    autoPlay
                    muted
                    style={{
                      width: '100%',
                      maxWidth: '300px',
                      borderRadius: '8px',
                      border: '1px solid #ccc'
                    }}
                  />
                </Box>
              )}
            </CardContent>
          </Card>

          {/* Screen Recording Permission */}
          <Card>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                <ScreenIcon color="primary" />
                <Box sx={{ flexGrow: 1 }}>
                  <Typography variant="h6">Screen Recording</Typography>
                  <Typography variant="body2" color="text.secondary">
                    Optional for sharing your screen during coding questions
                  </Typography>
                </Box>
                <Chip
                  icon={getStatusIcon(permissions.screen.status)}
                  label={getStatusText(permissions.screen.status)}
                  color={getStatusColor(permissions.screen.status)}
                />
              </Box>
            </CardContent>
          </Card>
        </Box>

        {isChecking && (
          <Box sx={{ mb: 3 }}>
            <Typography variant="body2" sx={{ mb: 1 }}>
              Checking permissions...
            </Typography>
            <LinearProgress />
          </Box>
        )}

        <Box sx={{ display: 'flex', gap: 2 }}>
          <Button
            variant="outlined"
            onClick={checkAllPermissions}
            disabled={isChecking}
            sx={{ flex: 1 }}
          >
            Check Permissions
          </Button>
          <Button
            variant="contained"
            onClick={handleContinue}
            disabled={!permissions.microphone.granted || !permissions.camera.granted}
            sx={{ flex: 1 }}
          >
            Continue to Instructions
          </Button>
        </Box>
      </Paper>
    </Container>
  );
};

export default Permissions;
