import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  Box,
  Button,
  Alert,
  Card,
  CardContent,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Divider,
  Checkbox,
  Stepper,
  Step,
  StepLabel,
  Avatar,
  Fade,
  LinearProgress,
  Chip,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Tooltip
} from '@mui/material';
import {
  Warning as WarningIcon,
  Info as InfoIcon,
  CheckCircle as CheckIcon,
  Error as ErrorIcon,
  Timer as TimerIcon,
  ExpandMore as ExpandMoreIcon,
  Security as SecurityIcon,
  Settings as SettingsIcon,
  PlayArrow as PlayIcon,
  Assignment as AssignmentIcon,
  Gavel as GavelIcon,
  VerifiedUser as VerifiedIcon
} from '@mui/icons-material';

const Instructions = () => {
  const navigate = useNavigate();
  const [acknowledged, setAcknowledged] = useState(false);
  const [readSections, setReadSections] = useState({});
  const [currentSection, setCurrentSection] = useState(0);

  const sections = [
    {
      id: 'preparation',
      title: 'Pre-Interview Preparation',
      icon: SettingsIcon,
      color: 'primary',
      required: true,
      content: [
        {
          title: 'Technical Setup',
          items: [
            'Ensure stable internet connection (5+ Mbps recommended)',
            'Test camera and microphone functionality',
            'Use a quiet, well-lit environment',
            'Close unnecessary applications and browser tabs',
            'Disable notifications and system sounds'
          ]
        },
        {
          title: 'Environment Setup',
          items: [
            'Choose a professional background',
            'Ensure adequate lighting on your face',
            'Position camera at eye level',
            'Test audio levels in your environment',
            'Have water available if needed'
          ]
        }
      ]
    },
    {
      id: 'rules',
      title: 'Interview Rules & Guidelines',
      icon: GavelIcon,
      color: 'error',
      required: true,
      content: [
        {
          title: 'Prohibited Actions',
          severity: 'error',
          items: [
            'Do not look away from camera for extended periods',
            'Do not use external assistance or second monitors',
            'Do not leave the camera frame during interview',
            'Do not use headphones or earbuds (audio must be clear)',
            'Do not interrupt the interviewer',
            'Do not have unauthorized persons in the room'
          ]
        },
        {
          title: 'Required Conduct',
          severity: 'info',
          items: [
            'Maintain professional appearance and demeanor',
            'Answer questions clearly and completely',
            'Stay engaged and focused throughout',
            'Be honest and authentic in responses',
            'Respect the interview process and timing'
          ]
        }
      ]
    },
    {
      id: 'process',
      title: 'Interview Process',
      icon: PlayIcon,
      color: 'info',
      required: true,
      content: [
        {
          title: 'How It Works',
          items: [
            'Questions are presented one at a time',
            'You have unlimited time to prepare your answer',
            'Click record when ready to answer',
            'Speak clearly and at normal pace',
            'You may be asked follow-up questions'
          ]
        },
        {
          title: 'Technical Features',
          items: [
            'Screen sharing available for coding questions',
            'Real-time audio/video recording',
            'Automatic transcription of responses',
            'AI-powered feedback generation',
            'Detailed performance analytics'
          ]
        }
      ]
    },
    {
      id: 'privacy',
      title: 'Privacy & Data Usage',
      icon: SecurityIcon,
      color: 'warning',
      required: true,
      content: [
        {
          title: 'Data Collection',
          items: [
            'Audio and video recordings for evaluation',
            'Facial analysis for engagement tracking',
            'Speech pattern analysis for communication assessment',
            'Response transcription for content analysis',
            'Performance metrics and timing data'
          ]
        },
        {
          title: 'Data Protection',
          items: [
            'All data encrypted and securely stored',
            'Recordings used solely for interview assessment',
            'Personal information kept confidential',
            'Data retention limited to evaluation period',
            'Option to delete recordings after feedback'
          ]
        }
      ]
    },
    {
      id: 'acknowledgment',
      title: 'Final Acknowledgment',
      icon: VerifiedIcon,
      color: 'success',
      required: true,
      content: [
        {
          title: 'Terms Agreement',
          items: [
            'I understand all interview rules and guidelines',
            'I agree to comply with technical requirements',
            'I consent to audio/video recording and analysis',
            'I acknowledge consequences of rule violations',
            'I confirm my environment meets all requirements'
          ]
        }
      ]
    }
  ];

  const handleSectionRead = (sectionId) => {
    setReadSections(prev => ({
      ...prev,
      [sectionId]: true
    }));
  };

  const handleContinue = () => {
    if (!acknowledged) return;

    // Check if all required sections are read
    const allRequiredRead = sections
      .filter(section => section.required)
      .every(section => readSections[section.id]);

    if (!allRequiredRead) {
      return;
    }

    navigate('/interview');
  };

  const allSectionsRead = sections
    .filter(section => section.required)
    .every(section => readSections[section.id]);

  const progressValue = (Object.keys(readSections).length / sections.filter(s => s.required).length) * 100;

  return (
    <Box
      sx={{
        minHeight: '100vh',
        bgcolor: 'grey.50',
        py: 4
      }}
    >
      <Container maxWidth="lg">
        <Paper
          elevation={8}
          sx={{
            p: 4,
            background: 'linear-gradient(145deg, #ffffff 0%, #f8fafc 100%)'
          }}
        >
          {/* Header */}
          <Box sx={{ textAlign: 'center', mb: 4 }}>
            <Typography
              variant="h4"
              sx={{
                fontWeight: 700,
                background: 'linear-gradient(45deg, #2563eb, #7c3aed)',
                backgroundClip: 'text',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                mb: 1
              }}
            >
              Interview Guidelines
            </Typography>
            <Typography variant="h6" color="text.secondary">
              Please review all sections carefully before starting
            </Typography>
          </Box>

          {/* Progress Indicator */}
          <Box sx={{ mb: 4 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 1 }}>
              <Typography variant="body2" color="text.secondary">
                Reading Progress
              </Typography>
              <Chip
                size="small"
                label={`${Object.keys(readSections).length}/${sections.filter(s => s.required).length} sections read`}
                color={allSectionsRead ? "success" : "default"}
              />
            </Box>
            <LinearProgress
              variant="determinate"
              value={progressValue}
              sx={{
                height: 8,
                borderRadius: 4,
                bgcolor: 'grey.200',
                '& .MuiLinearProgress-bar': {
                  background: 'linear-gradient(45deg, #2563eb, #7c3aed)',
                  borderRadius: 4
                }
              }}
            />
          </Box>

          {/* Critical Warning */}
          <Fade in={true} timeout={1000}>
            <Alert
              severity="error"
              sx={{
                mb: 4,
                borderRadius: 3,
                border: '2px solid',
                borderColor: 'error.main'
              }}
              icon={<WarningIcon fontSize="large" />}
            >
              <Typography variant="h6" sx={{ fontWeight: 600, mb: 1 }}>
                ⚠️ Critical Warning
              </Typography>
              <Typography variant="body2">
                Violation of any instruction may result in immediate termination of your interview session
                without prior notice. Please read all sections carefully and ensure compliance.
              </Typography>
            </Alert>
          </Fade>

          {/* Interactive Sections */}
          <Box sx={{ mb: 4 }}>
            {sections.map((section, index) => {
              const Icon = section.icon;
              const isRead = readSections[section.id];
              const isExpanded = currentSection === index;

              return (
                <Fade in={true} key={section.id} timeout={500 + index * 100}>
                  <Accordion
                    expanded={isExpanded}
                    onChange={() => {
                      setCurrentSection(isExpanded ? -1 : index);
                      if (!isRead) {
                        handleSectionRead(section.id);
                      }
                    }}
                    sx={{
                      mb: 2,
                      borderRadius: 3,
                      border: `2px solid ${isRead ? '#10b981' : '#e5e7eb'}`,
                      '&:before': { display: 'none' },
                      boxShadow: isRead ? '0 4px 20px rgba(16, 185, 129, 0.1)' : '0 2px 8px rgba(0, 0, 0, 0.08)',
                      transition: 'all 0.3s ease'
                    }}
                  >
                    <AccordionSummary
                      expandIcon={<ExpandMoreIcon />}
                      sx={{
                        borderRadius: 3,
                        '& .MuiAccordionSummary-content': {
                          alignItems: 'center',
                          gap: 2
                        }
                      }}
                    >
                      <Avatar
                        sx={{
                          bgcolor: isRead ? 'success.main' : `${section.color}.main`,
                          width: 40,
                          height: 40
                        }}
                      >
                        <Icon sx={{ fontSize: 20 }} />
                      </Avatar>

                      <Box sx={{ flexGrow: 1 }}>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 0.5 }}>
                          <Typography variant="h6" sx={{ fontWeight: 600 }}>
                            {section.title}
                          </Typography>
                          {section.required && (
                            <Chip
                              label="Required"
                              size="small"
                              color="error"
                              variant="outlined"
                            />
                          )}
                          {isRead && (
                            <CheckIcon color="success" sx={{ fontSize: 20 }} />
                          )}
                        </Box>
                        <Typography variant="body2" color="text.secondary">
                          {section.content.length} subsection{section.content.length !== 1 ? 's' : ''} • Click to {isExpanded ? 'collapse' : 'expand'}
                        </Typography>
                      </Box>
                    </AccordionSummary>

                    <AccordionDetails sx={{ pt: 0 }}>
                      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
                        {section.content.map((subsection, subIndex) => (
                          <Box key={subIndex}>
                            <Typography
                              variant="subtitle1"
                              sx={{
                                fontWeight: 600,
                                mb: 2,
                                color: subsection.severity === 'error' ? 'error.main' : 'text.primary',
                                display: 'flex',
                                alignItems: 'center',
                                gap: 1
                              }}
                            >
                              {subsection.severity === 'error' && <ErrorIcon />}
                              {subsection.severity === 'info' && <InfoIcon />}
                              {subsection.title}
                            </Typography>

                            <List dense sx={{ bgcolor: 'grey.50', borderRadius: 2, p: 2 }}>
                              {subsection.items.map((item, itemIndex) => (
                                <ListItem key={itemIndex} sx={{ px: 0, py: 0.5 }}>
                                  <ListItemIcon sx={{ minWidth: 32 }}>
                                    {subsection.severity === 'error' ? (
                                      <ErrorIcon color="error" fontSize="small" />
                                    ) : (
                                      <CheckIcon color="success" fontSize="small" />
                                    )}
                                  </ListItemIcon>
                                  <ListItemText
                                    primary={item}
                                    primaryTypographyProps={{
                                      variant: 'body2',
                                      sx: { fontWeight: subsection.severity === 'error' ? 500 : 400 }
                                    }}
                                  />
                                </ListItem>
                              ))}
                            </List>
                          </Box>
                        ))}
                      </Box>
                    </AccordionDetails>
                  </Accordion>
                </Fade>
              );
            })}
          </Box>

          {/* Final Acknowledgment */}
          <Fade in={allSectionsRead} timeout={500}>
            <Card
              sx={{
                mb: 4,
                border: `2px solid ${acknowledged ? '#10b981' : '#e5e7eb'}`,
                bgcolor: acknowledged ? 'success.50' : 'background.paper'
              }}
            >
              <CardContent sx={{ p: 3 }}>
                <Box sx={{ display: 'flex', alignItems: 'flex-start', gap: 2 }}>
                  <VerifiedIcon
                    color={acknowledged ? "success" : "action"}
                    sx={{ fontSize: 24, mt: 0.5 }}
                  />
                  <Box sx={{ flexGrow: 1 }}>
                    <Typography variant="h6" sx={{ mb: 2, fontWeight: 600 }}>
                      Final Acknowledgment
                    </Typography>

                    <Box sx={{ display: 'flex', alignItems: 'flex-start', gap: 1, mb: 2 }}>
                      <Checkbox
                        checked={acknowledged}
                        onChange={(e) => setAcknowledged(e.target.checked)}
                        color="success"
                        disabled={!allSectionsRead}
                      />
                      <Box>
                        <Typography variant="body1" sx={{ mb: 1 }}>
                          I have read, understood, and agree to comply with all the instructions and terms above.
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          By checking this box, you acknowledge that violation of any instruction may result in
                          immediate termination of your interview session without prior notice.
                        </Typography>
                      </Box>
                    </Box>

                    {!allSectionsRead && (
                      <Alert severity="warning" sx={{ borderRadius: 2 }}>
                        <Typography variant="body2">
                          Please read all required sections before proceeding.
                        </Typography>
                      </Alert>
                    )}
                  </Box>
                </Box>
              </CardContent>
            </Card>
          </Fade>

          {/* Action Button */}
          <Button
            variant="contained"
            onClick={handleContinue}
            disabled={!acknowledged || !allSectionsRead}
            fullWidth
            size="large"
            sx={{
              py: 2,
              fontSize: '1.1rem',
              fontWeight: 600,
              borderRadius: 3,
              background: acknowledged && allSectionsRead
                ? 'linear-gradient(45deg, #059669, #10b981)'
                : 'grey.400',
              '&:hover': {
                background: acknowledged && allSectionsRead
                  ? 'linear-gradient(45deg, #047857, #059669)'
                  : 'grey.500',
              },
              boxShadow: acknowledged && allSectionsRead
                ? '0 8px 32px rgba(5, 150, 105, 0.3)'
                : 'none'
            }}
          >
            {acknowledged && allSectionsRead ? (
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                <PlayIcon />
                Start My Interview
              </Box>
            ) : (
              'Complete All Requirements to Continue'
            )}
          </Button>
        </Paper>
      </Container>
    </Box>
  );
};

export default Instructions;
