import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Alert,
  Chip,
  Stepper,
  Step,
  StepLabel,
  Card,
  CardContent,
  Avatar,
  Fade,
  CircularProgress,
  LinearProgress,
  InputAdornment,
  Tooltip,
  IconButton
} from '@mui/material';
import {
  Work as WorkIcon,
  Business as BusinessIcon,
  Description as DescriptionIcon,
  Assessment as AssessmentIcon,
  CloudUpload as UploadIcon,
  CheckCircle as CheckIcon,
  Error as ErrorIcon,
  Info as InfoIcon,
  Clear as ClearIcon
} from '@mui/icons-material';
import { useDropzone } from 'react-dropzone';

const JobDetails = () => {
  const navigate = useNavigate();
  const [activeStep, setActiveStep] = useState(0);
  const [completed, setCompleted] = useState({});
  const [formData, setFormData] = useState({
    jobProfile: '',
    jobSubProfile: '',
    jobDescription: '',
    companyName: '',
    difficulty: '',
    cvFile: null
  });
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const steps = [
    { label: 'Job Profile', icon: WorkIcon, required: true },
    { label: 'Job Details', icon: DescriptionIcon, required: true },
    { label: 'Company Info', icon: BusinessIcon, required: false },
    { label: 'Resume', icon: UploadIcon, required: false },
    { label: 'Difficulty', icon: AssessmentIcon, required: true }
  ];

  const jobProfiles = {
    'AI/ML Engineering': [
      'Machine Learning Engineer',
      'Deep Learning Engineer',
      'NLP Engineer',
      'Computer Vision Engineer',
      'Data Scientist',
      'AI Research Engineer'
    ],
    'Software Engineering': [
      'Frontend Developer',
      'Backend Developer',
      'Full Stack Developer',
      'DevOps Engineer',
      'Mobile App Developer',
      'System Architect',
      'QA Engineer'
    ],
    'Data Engineering': [
      'Data Engineer',
      'ETL Developer',
      'Big Data Engineer',
      'Data Warehouse Engineer'
    ],
    'Cybersecurity': [
      'Security Analyst',
      'Penetration Tester',
      'Security Engineer',
      'SOC Analyst'
    ],
    'Cloud Engineering': [
      'AWS Engineer',
      'Azure Engineer',
      'GCP Engineer',
      'Cloud Architect'
    ],
    'Product Management': [
      'Product Manager',
      'Technical Product Manager',
      'Product Owner'
    ]
  };

  const handleInputChange = (field) => (e) => {
    const value = e.target.value;
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));

    // Clear error for this field
    if (errors[field]) {
      setErrors(prev => ({
        ...prev,
        [field]: ''
      }));
    }

    // Reset sub-profile when main profile changes
    if (field === 'jobProfile') {
      setFormData(prev => ({
        ...prev,
        jobSubProfile: ''
      }));
    }
  };

  const validateStep = (step) => {
    const newErrors = {};

    switch (step) {
      case 0: // Job Profile
        if (!formData.jobProfile) {
          newErrors.jobProfile = 'Please select a job profile';
        }
        break;
      case 1: // Job Details
        if (!formData.jobDescription.trim()) {
          newErrors.jobDescription = 'Job description is required';
        } else if (formData.jobDescription.trim().length < 50) {
          newErrors.jobDescription = 'Job description must be at least 50 characters';
        }
        break;
      case 4: // Difficulty
        if (!formData.difficulty) {
          newErrors.difficulty = 'Please select difficulty level';
        }
        break;
      default:
        break;
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleNext = () => {
    if (validateStep(activeStep)) {
      setCompleted(prev => ({
        ...prev,
        [activeStep]: true
      }));
      setActiveStep(prev => prev + 1);
    }
  };

  const handleBack = () => {
    setActiveStep(prev => prev - 1);
  };

  const handleStepClick = (step) => {
    // Allow going back to completed steps
    if (step < activeStep || completed[step - 1] || step === 0) {
      setActiveStep(step);
    }
  };

  const onDrop = (acceptedFiles) => {
    if (acceptedFiles.length > 0) {
      const file = acceptedFiles[0];
      // Validate file size (max 10MB)
      if (file.size > 10 * 1024 * 1024) {
        setErrors(prev => ({
          ...prev,
          cvFile: 'File size must be less than 10MB'
        }));
        return;
      }

      setFormData(prev => ({
        ...prev,
        cvFile: file
      }));

      if (errors.cvFile) {
        setErrors(prev => ({
          ...prev,
          cvFile: ''
        }));
      }
    }
  };

  const removeFile = () => {
    setFormData(prev => ({
      ...prev,
      cvFile: null
    }));
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/msword': ['.doc'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx']
    },
    multiple: false,
    maxSize: 10 * 1024 * 1024 // 10MB
  });

  const handleSubmit = async () => {
    if (!validateStep(activeStep)) return;

    setIsSubmitting(true);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Store form data
      localStorage.setItem('jobDetails', JSON.stringify(formData));
      navigate('/permissions');
    } catch (error) {
      console.error('Submission error:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const renderStepContent = (step) => {
    switch (step) {
      case 0: // Job Profile
        return (
          <Fade in={true} timeout={500}>
            <Box sx={{ mt: 3 }}>
              <Typography variant="h6" gutterBottom sx={{ mb: 3, color: 'primary.main' }}>
                Select Your Target Job Profile
              </Typography>

              <FormControl fullWidth error={!!errors.jobProfile} sx={{ mb: 3 }}>
                <InputLabel>Job Category *</InputLabel>
                <Select
                  value={formData.jobProfile}
                  onChange={handleInputChange('jobProfile')}
                  label="Job Category *"
                  startAdornment={
                    <InputAdornment position="start">
                      <WorkIcon color="action" />
                    </InputAdornment>
                  }
                >
                  {Object.keys(jobProfiles).map((profile) => (
                    <MenuItem key={profile} value={profile}>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <WorkIcon fontSize="small" />
                        {profile}
                      </Box>
                    </MenuItem>
                  ))}
                </Select>
                {errors.jobProfile && (
                  <Typography variant="caption" color="error" sx={{ mt: 1, display: 'flex', alignItems: 'center', gap: 0.5 }}>
                    <ErrorIcon fontSize="small" />
                    {errors.jobProfile}
                  </Typography>
                )}
              </FormControl>

              {formData.jobProfile && (
                <Fade in={true}>
                  <FormControl fullWidth sx={{ mb: 2 }}>
                    <InputLabel>Specialization (Optional)</InputLabel>
                    <Select
                      value={formData.jobSubProfile}
                      onChange={handleInputChange('jobSubProfile')}
                      label="Specialization (Optional)"
                    >
                      {jobProfiles[formData.jobProfile].map((subProfile) => (
                        <MenuItem key={subProfile} value={subProfile}>
                          {subProfile}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                </Fade>
              )}

              <Alert severity="info" sx={{ mt: 3 }}>
                <Typography variant="body2">
                  <strong>Tip:</strong> Choose the job profile that best matches your target position.
                  This helps us generate more relevant interview questions.
                </Typography>
              </Alert>
            </Box>
          </Fade>
        );

      case 1: // Job Details
        return (
          <Fade in={true} timeout={500}>
            <Box sx={{ mt: 3 }}>
              <Typography variant="h6" gutterBottom sx={{ mb: 3, color: 'primary.main' }}>
                Job Description & Requirements
              </Typography>

              <TextField
                fullWidth
                multiline
                rows={8}
                label="Job Description *"
                placeholder="Paste the complete job description here. Include responsibilities, requirements, skills needed, and any specific technologies mentioned..."
                value={formData.jobDescription}
                onChange={handleInputChange('jobDescription')}
                error={!!errors.jobDescription}
                helperText={errors.jobDescription || `${formData.jobDescription.length}/500 characters minimum`}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <DescriptionIcon color="action" sx={{ mt: 1 }} />
                    </InputAdornment>
                  ),
                }}
                sx={{ mb: 2 }}
              />

              <Box sx={{ display: 'flex', gap: 1, mb: 2 }}>
                <Chip
                  icon={<InfoIcon />}
                  label="Include technical skills"
                  variant="outlined"
                  size="small"
                />
                <Chip
                  icon={<InfoIcon />}
                  label="Mention experience level"
                  variant="outlined"
                  size="small"
                />
                <Chip
                  icon={<InfoIcon />}
                  label="List key responsibilities"
                  variant="outlined"
                  size="small"
                />
              </Box>

              <Alert severity="success" sx={{ mt: 2 }}>
                <Typography variant="body2">
                  <strong>Great!</strong> Detailed job descriptions help us create more accurate and relevant interview questions.
                </Typography>
              </Alert>
            </Box>
          </Fade>
        );

      case 2: // Company Info
        return (
          <Fade in={true} timeout={500}>
            <Box sx={{ mt: 3 }}>
              <Typography variant="h6" gutterBottom sx={{ mb: 3, color: 'primary.main' }}>
                Company Information
              </Typography>

              <TextField
                fullWidth
                label="Company Name"
                placeholder="e.g., Google, Microsoft, Amazon, or your target company"
                value={formData.companyName}
                onChange={handleInputChange('companyName')}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <BusinessIcon color="action" />
                    </InputAdornment>
                  ),
                }}
                sx={{ mb: 3 }}
              />

              <Alert severity="info">
                <Typography variant="body2">
                  <strong>Optional:</strong> Providing company information helps us tailor questions to match
                  the company's culture and specific requirements.
                </Typography>
              </Alert>
            </Box>
          </Fade>
        );

      case 3: // Resume
        return (
          <Fade in={true} timeout={500}>
            <Box sx={{ mt: 3 }}>
              <Typography variant="h6" gutterBottom sx={{ mb: 3, color: 'primary.main' }}>
                Upload Your Resume
              </Typography>

              <Box
                {...getRootProps()}
                sx={{
                  border: `2px dashed ${isDragActive ? 'primary.main' : 'grey.300'}`,
                  borderRadius: 3,
                  p: 4,
                  textAlign: 'center',
                  cursor: 'pointer',
                  bgcolor: isDragActive ? 'primary.50' : 'grey.50',
                  transition: 'all 0.3s ease',
                  '&:hover': {
                    borderColor: 'primary.main',
                    bgcolor: 'primary.50'
                  }
                }}
              >
                <input {...getInputProps()} />

                {formData.cvFile ? (
                  <Box>
                    <Avatar sx={{ width: 60, height: 60, bgcolor: 'success.main', mx: 'auto', mb: 2 }}>
                      <CheckIcon sx={{ fontSize: 30 }} />
                    </Avatar>
                    <Typography variant="h6" color="success.main" gutterBottom>
                      Resume Uploaded Successfully
                    </Typography>
                    <Typography variant="body1" sx={{ mb: 1 }}>
                      {formData.cvFile.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                      {(formData.cvFile.size / 1024 / 1024).toFixed(2)} MB
                    </Typography>
                    <Button
                      variant="outlined"
                      startIcon={<ClearIcon />}
                      onClick={(e) => {
                        e.stopPropagation();
                        removeFile();
                      }}
                      size="small"
                    >
                      Remove File
                    </Button>
                  </Box>
                ) : (
                  <Box>
                    <Avatar sx={{ width: 60, height: 60, bgcolor: 'primary.main', mx: 'auto', mb: 2 }}>
                      <UploadIcon sx={{ fontSize: 30 }} />
                    </Avatar>
                    <Typography variant="h6" gutterBottom>
                      {isDragActive ? 'Drop your resume here' : 'Upload Your Resume'}
                    </Typography>
                    <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                      Drag & drop your CV or click to browse
                    </Typography>
                    <Typography variant="caption" color="text.secondary">
                      Supports PDF, DOC, DOCX (Max 10MB)
                    </Typography>
                  </Box>
                )}
              </Box>

              {errors.cvFile && (
                <Typography variant="caption" color="error" sx={{ mt: 1, display: 'flex', alignItems: 'center', gap: 0.5 }}>
                  <ErrorIcon fontSize="small" />
                  {errors.cvFile}
                </Typography>
              )}

              <Alert severity="info" sx={{ mt: 3 }}>
                <Typography variant="body2">
                  <strong>Optional:</strong> Your resume helps us understand your background and create
                  more personalized interview questions.
                </Typography>
              </Alert>
            </Box>
          </Fade>
        );

      case 4: // Difficulty
        return (
          <Fade in={true} timeout={500}>
            <Box sx={{ mt: 3 }}>
              <Typography variant="h6" gutterBottom sx={{ mb: 3, color: 'primary.main' }}>
                Interview Difficulty Level
              </Typography>

              <FormControl fullWidth error={!!errors.difficulty} sx={{ mb: 3 }}>
                <InputLabel>Select Difficulty *</InputLabel>
                <Select
                  value={formData.difficulty}
                  onChange={handleInputChange('difficulty')}
                  label="Select Difficulty *"
                  startAdornment={
                    <InputAdornment position="start">
                      <AssessmentIcon color="action" />
                    </InputAdornment>
                  }
                >
                  <MenuItem value="easy">
                    <Box>
                      <Typography variant="subtitle2">Easy</Typography>
                      <Typography variant="caption" color="text.secondary">
                        Basic questions, suitable for entry-level positions
                      </Typography>
                    </Box>
                  </MenuItem>
                  <MenuItem value="medium">
                    <Box>
                      <Typography variant="subtitle2">Medium</Typography>
                      <Typography variant="caption" color="text.secondary">
                        Intermediate questions, for mid-level positions
                      </Typography>
                    </Box>
                  </MenuItem>
                  <MenuItem value="hard">
                    <Box>
                      <Typography variant="subtitle2">Hard</Typography>
                      <Typography variant="caption" color="text.secondary">
                        Advanced questions, for senior-level positions
                      </Typography>
                    </Box>
                  </MenuItem>
                </Select>
                {errors.difficulty && (
                  <Typography variant="caption" color="error" sx={{ mt: 1, display: 'flex', alignItems: 'center', gap: 0.5 }}>
                    <ErrorIcon fontSize="small" />
                    {errors.difficulty}
                  </Typography>
                )}
              </FormControl>

              <Alert severity="warning">
                <Typography variant="body2">
                  <strong>Important:</strong> Choose the difficulty level that matches your experience level
                  and the position you're targeting. This ensures appropriate challenge.
                </Typography>
              </Alert>
            </Box>
          </Fade>
        );

      default:
        return null;
    }
  };

  const isStepCompleted = (step) => completed[step];
  const isLastStep = activeStep === steps.length - 1;

  return (
    <Box
      sx={{
        minHeight: '100vh',
        bgcolor: 'grey.50',
        py: 4
      }}
    >
      <Container maxWidth="lg">
        <Paper
          elevation={8}
          sx={{
            p: 4,
            background: 'linear-gradient(145deg, #ffffff 0%, #f8fafc 100%)'
          }}
        >
          {/* Header */}
          <Box sx={{ textAlign: 'center', mb: 4 }}>
            <Typography
              variant="h4"
              sx={{
                fontWeight: 700,
                background: 'linear-gradient(45deg, #2563eb, #7c3aed)',
                backgroundClip: 'text',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                mb: 1
              }}
            >
              Job Interview Setup
            </Typography>
            <Typography variant="h6" color="text.secondary">
              Configure your personalized interview experience
            </Typography>
          </Box>

          {/* Progress Stepper */}
          <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
            {steps.map((step, index) => {
              const Icon = step.icon;
              const isCompleted = isStepCompleted(index);

              return (
                <Step key={step.label} completed={isCompleted}>
                  <StepLabel
                    onClick={() => handleStepClick(index)}
                    sx={{
                      cursor: (index < activeStep || isCompleted || index === 0) ? 'pointer' : 'default',
                      '& .MuiStepLabel-label': {
                        fontWeight: activeStep === index ? 600 : 400,
                        color: activeStep === index ? 'primary.main' : 'text.secondary'
                      }
                    }}
                    StepIconComponent={() => (
                      <Avatar
                        sx={{
                          bgcolor: isCompleted ? 'success.main' : activeStep === index ? 'primary.main' : 'grey.300',
                          width: 40,
                          height: 40
                        }}
                      >
                        <Icon sx={{ fontSize: 20, color: 'white' }} />
                      </Avatar>
                    )}
                  >
                    {step.label}
                    {step.required && <span style={{ color: 'red' }}> *</span>}
                  </StepLabel>
                </Step>
              );
            })}
          </Stepper>

          {/* Step Content */}
          <Box sx={{ minHeight: 400 }}>
            {renderStepContent(activeStep)}
          </Box>

          {/* Navigation Buttons */}
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 4 }}>
            <Button
              disabled={activeStep === 0}
              onClick={handleBack}
              variant="outlined"
              sx={{ minWidth: 120 }}
            >
              Back
            </Button>

            <Button
              variant="contained"
              onClick={isLastStep ? handleSubmit : handleNext}
              disabled={isSubmitting}
              sx={{
                minWidth: 120,
                background: 'linear-gradient(45deg, #2563eb, #7c3aed)',
                '&:hover': {
                  background: 'linear-gradient(45deg, #1d4ed8, #5b21b6)',
                }
              }}
            >
              {isSubmitting ? (
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <CircularProgress size={20} color="inherit" />
                  Processing...
                </Box>
              ) : isLastStep ? (
                'Start Interview'
              ) : (
                'Next'
              )}
            </Button>
          </Box>

          {/* Progress Bar */}
          <Box sx={{ mt: 3 }}>
            <LinearProgress
              variant="determinate"
              value={((activeStep + 1) / steps.length) * 100}
              sx={{
                height: 8,
                borderRadius: 4,
                bgcolor: 'grey.200',
                '& .MuiLinearProgress-bar': {
                  background: 'linear-gradient(45deg, #2563eb, #7c3aed)',
                  borderRadius: 4
                }
              }}
            />
            <Typography variant="body2" color="text.secondary" align="center" sx={{ mt: 1 }}>
              Step {activeStep + 1} of {steps.length}
            </Typography>
          </Box>
        </Paper>
      </Container>
    </Box>
  );
};

export default JobDetails;
